import React, { createContext, useState, useContext, useEffect } from 'react';
import { toast } from 'react-toastify';
import api, { testConnection } from '../utils/api';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth deve ser usado dentro de um AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const checkAuth = async () => {
      try {
        // Testar conexão primeiro
        const isConnected = await testConnection();
        if (!isConnected) {
          console.warn('⚠️ Não foi possível conectar com a API');
        }

        const token = localStorage.getItem('token');
        if (token) {
          const response = await api.get('/auth/me');
          setUser(response.data.user);
        }
      } catch (error) {
        console.error('Erro na verificação de autenticação:', error);
        localStorage.removeItem('token');
        localStorage.removeItem('user');
      } finally {
        setLoading(false);
      }
    };

    checkAuth();
  }, []);

  const login = async (email, password) => {
    try {
      const response = await axios.post('/auth/login', {
        email,
        password
      });

      const { token, user: userData } = response.data;
      
      localStorage.setItem('token', token);
      localStorage.setItem('user', JSON.stringify(userData));
      setUser(userData);
      
      toast.success('Login realizado com sucesso!');
      return userData;
    } catch (error) {
      const message = error.response?.data?.message || 'Erro ao fazer login';
      toast.error(message);
      throw new Error(message);
    }
  };

  const logout = async () => {
    try {
      await axios.post('/auth/logout');
    } catch (error) {
      // Ignorar erros de logout
    } finally {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      setUser(null);
      toast.info('Logout realizado com sucesso');
    }
  };

  const updatePassword = async (currentPassword, newPassword) => {
    try {
      // Simulação de atualização de senha
      // Em produção, fazer chamada para API
      toast.success('Senha atualizada com sucesso!');
      return true;
    } catch (error) {
      toast.error('Erro ao atualizar senha');
      throw error;
    }
  };

  const resetPassword = async (email) => {
    try {
      // Simulação de reset de senha
      // Em produção, fazer chamada para API
      toast.success(`Email de recuperação enviado para ${email}`);
      return true;
    } catch (error) {
      toast.error('Erro ao enviar email de recuperação');
      throw error;
    }
  };

  const value = {
    user,
    login,
    logout,
    updatePassword,
    resetPassword,
    loading
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
